mapper.select:
select s.method,s.etd,s.`desc`,s.eta,s.mudigang,o.dest,f.volume,f.weight,f.ctn,a.openid,s.qiyungang,d.cut_time,d.cut_end,o.status,f.ups,c.contact_address,c.contact_phone,o.numbers,o.id,s.user_id,o.address_id
 from fba.ship_schdule as s,fba.schdule_details as d,fba.orders as o,fba.subscriber_address as a,fba.supplier_address as c,fba.orders_details as f
 where s.id=d.ship_id and s.id=o.item_id and a.id=o.address_id and s.user_id=c.user_id and a.openid=#{openid} and f.order_id=o.id order by o.id ;

 select s.user_id,s.`desc`,s.eta,s.etd,s.id,s.`method`,s.mudigang,s.qiyungang,s.status,d.weight,d.volume,d.ship_id,d.cut_time,d.cut_end,c.contact_address,c.contact_phone,d.money,d.dead_date,d.compensate,c.peichang,c.company_name,d.kill_start,d.kill_end,d.discount
from fba.ship_schdule as s,fba.schdule_details as d,supplier_address as c
 where s.status=0 and s.user_id=c.user_id and s.id=d.ship_id and d.volume>0 and d.weight>0 and s.number like 'SP%' and d.kill_end>=#{date} and #{date}>=d.kill_start order by s.top desc, s.etd asc ;

mapper.update:
 update schdule_details set
weight=weight-#{weight},volume=volume-#{volume}
where ship_id=#{id}
and fba.schdule_details.weight>=#{weight} and fba.schdule_details.volume>=#{volume}
